import React, { useEffect, useState } from "react";
import Form from "../components/Form";
import ValidLogin from "../components/ValidLogin";
import FetchRequest from "../function/utility";
export default function LoginPage(props) {
  const [errorMessages, setErrorMessages] = useState([]);
  const [notFoundPermissionsMessage, setNotFoundPermissionsMessage] =
    useState("");

  const inputField = [
    {
      name: "name",
      label: "name",
      placeholder: "name",
      type: "name",
    },
    {
      name: "password",
      label: "Password",
      placeholder: "••••••••",
      type: "password",
    },
  ];

  const LoginPage = async (inputValue) => {
    setNotFoundPermissionsMessage("");
    setErrorMessages([]);
    await ValidLogin(inputValue)
      .then(() => {
        const request = {
          method: "POST",
          url: "api/login",
          body: JSON.stringify({ inputValue }),
          ContentType: "application/json; charset=UTF-8",
        };

        FetchRequest(request)
          .then((userInfo) => {

            props.getUserInfo(userInfo[0]);
          })
          .catch((errorData) => {
            setNotFoundPermissionsMessage(errorData.err);
          });
      })
      .catch((error) => {
        setErrorMessages(error);
      });
  };

  return (
    <div>
      <Form
        inputField={inputField}
        submit={LoginPage}
        buttonText={"Sign Up"}
        errorMessages={errorMessages}
        notFoundPermissionsMessage={notFoundPermissionsMessage}
      />
    </div>
  );
}
