import { jwtDecode } from "jwt-decode";

export  default class Utility{

  static async FetchRequest(request) {
    return new Promise((resolve, reject) => {
      fetch(`${process.env.REACT_APP_API_URL}/${request.url}`, {
        method: request.method,
        body: request.method === "GET" ? null : request.body,
        headers: {
          "Content-Type": request.ContentType,
          ...(request.Authorization !== undefined && { "Authorization": `Bearer ${sessionStorage.getItem("accessToken")}` }),
        },
      })
        .then((response) => {
          if (response.status !== 200) {
            response.json().then((errorData) => {
              reject(errorData);
            });
            return;
          }
          response
            .json()
            .then((responseData) => {
              resolve(responseData);
            })
            .catch((err) => {
              reject(err);
            });
        })
        .catch((err) => {
          if (err.message === "Failed to fetch") {
            alert("Something went wrong.");
          }
          reject({ error: err });
        });
    });
  }

  static UserUnauthorized (){
    sessionStorage.removeItem("accessToken");
    sessionStorage.removeItem("refreshToken");
    alert("Unauthorized")
    window.location.reload();
  }

  static CheckAccessTokenExpiresIn(){
    return new Promise((resolve, reject) => {
      try {
        const decodedToken = jwtDecode(sessionStorage.getItem("accessToken"));

        const expirationTime = decodedToken.exp * 1000;

        if (expirationTime < Date.now())
        return resolve("Access token has expired");
        resolve(decodedToken);
      } catch (error) {
        reject(new Error("Failed to decode access token"));
      }
    });
  }
  static MakeNewAccessToken(newAccessToken){
    return new Promise((resolve, reject) => {
      try {
        sessionStorage.removeItem("accessToken");
        sessionStorage.setItem("accessToken", newAccessToken);
        resolve(true);
      } catch (error) {
        console.error(error);
        reject(false);
      }
    });
  }
}
