import React, { useEffect, useState } from "react";
import Form from "../components/Form";
import ValidLogin from "../components/ValidLogin";
export default function LoginPage(props) {
  const [errorMessages, setErrorMessages] = useState([]);
  const [notFoundPermissionsMessage, setNotFoundPermissionsMessage] =
    useState("");
  const user = { name: "david", password: "david" };

  const inputField = [
    {
      name: "name",
      label: "name",
      placeholder: "name",
      type: "name",
    },
    {
      name: "password",
      label: "Password",
      placeholder: "••••••••",
      type: "password",
    },
  ];
  const unauthorizedHendler = () => {
    setNotFoundPermissionsMessage(
      "User not found. Please check your information or sign up to create an account.",
    );
  };

  const LoginPage = async (inputValue) => {
    setNotFoundPermissionsMessage("");
    setErrorMessages([]);
    await ValidLogin(inputValue)
      .then(() => {
        console.log(1);
      })
      .catch((error) => {
        setErrorMessages(error);
      });
  };

  return (
    <div>
      <Form
        inputField={inputField}
        submit={LoginPage}
        buttonText={"Sign Up"}
        errorMessages={errorMessages}
        notFoundPermissionsMessage={notFoundPermissionsMessage}
      />
    </div>
  );
}
