import React,{useState,useEffect} from "react";
import Form from "./Form";
import ValidLogin from "./ValidLogin";
export default function Login(props){
    const [errorMessages, setErrorMessages] = useState([]);
  const [notFoundPermissionsMessage, setNotFoundPermissionsMessage] =  useState(props.notFoundPermissionsMessage);
  useEffect(() => {
    setNotFoundPermissionsMessage(props.notFoundPermissionsMessage);
  }, [props.notFoundPermissionsMessage]);
  const inputField = [
    {
      name: "name",
      label: "name",
      placeholder: "name",
      type: "name",
    },
    {
      name: "password",
      label: "Password",
      placeholder: "••••••••",
      type: "password",
    },
  ];
  function handleLogin(inputValue) {
    setErrorMessages([]);

    ValidLogin(inputValue)
      .then(() => {
        props.handleAuthentication(inputValue);
        // Assuming props.handleAuthentication updates notFoundPermissionsMessage internally
      })
      .catch((error) => {
        setNotFoundPermissionsMessage(""); // Set to an empty string when login fails
        setErrorMessages(error);
      });
  }
 console.log("notFoundPermissionsMessage:", notFoundPermissionsMessage);

    return(
        <div>
   <Form
        inputField={inputField}
        submit={handleLogin}
        buttonText={"Sign Up"}
        errorMessages={errorMessages}
        notFoundPermissionsMessage={notFoundPermissionsMessage}
      />

        </div>
    )
}
