import { jwtDecode } from "jwt-decode";

export  default class Utility{


  FetchRequest(){
    try {
      return new Promise((resolve, reject) => {
        fetch(`${process.env.REACT_APP_API_URL}/${request.url}`, {
          method: request.method,
          body: request.method === "GET" ? null : request.body,
          headers: {
            "Content-Type": request.ContentType,
            // ...(someCondition && { "Authorization": `Bearer ${accessToken}` }),
          },
        }).then((response) => {
          if (response.status !== 200) {
            response.json().then((errorData) => {
              reject(errorData);
            });
          } else {
            response
              .json()
              .then((responseData) => {
                resolve(responseData);
              })
              .catch((err) => {
                console.log(err);
              });
          }
        });
      });
    } catch (err) {
      console.log(err);
    }
  }
  checkAccessTokenExpiresIn(){
    return new Promise((resolve, reject) => {
      try {
        const decodedToken = jwtDecode(sessionStorage.getItem("accessToken"));

        const expirationTime = decodedToken.exp * 1000;

        if (expirationTime < Date.now())
          return reject("Access token has expired");
        resolve(decodedToken);
      } catch (error) {
        reject(new Error("Failed to decode access token"));
      }
    });
  }
  makeNewAccessToken(){
    return new Promise((resolve, reject) => {
      try {
        sessionStorage.removeItem("accessToken");
        sessionStorage.setItem("accessToken", newAccessToken);
        resolve(true);
      } catch (error) {
        console.error(error);
        reject(false);
      }
    });
  }
}
